mapper.select:
	   SELECT c_name,j_name,e_name,nw_name,psd,img,ban,desc_nw,proprietary,hash_code FROM t_mag_tra WHERE type=#{mag.type}
	   
	       AND hash_code IN
           
	            #{id}
           
	   
	   
	       AND c_name LIKE CONCAT('%',#{mag.name},'%')
	   
	   
	       AND ban=#{mag.limit}
	   
	   LIMIT #{start},#{row}

	   SELECT COUNT(*) FROM t_mag_tra WHERE type=#{mag.type}
	   
	       AND hash_code IN
           
	            #{id}
           
	   
	   
	       AND c_name LIKE CONCAT('%',#{mag.name},'%')
	   
	   
	       AND ban=#{mag.limit}
