mapper.select:
	   SELECT c_name,j_name,e_name,nw_name,psd,lev,attribute,race,atk,def,pend,link,img,ban,desc_nw,proprietary,hash_code FROM t_monster WHERE 1=1
	   
	       AND hash_code IN
           
	            #{id}
           
	   
	   
	       AND c_name LIKE CONCAT('%',#{mon.name},'%')
	   
	   
	       AND lev=#{mon.lev}
	   
	   
	       AND attribute IN
           
	            #{attribute}
           
	   
	   
	       AND race IN
           
	            #{race}
           
	   
	   
	       AND atk=#{mon.atk}
	   
	   
	       AND def=#{mon.def}
	   
	   
	       AND pend=#{mon.pend}
	   
	   
	       AND link=#{mon.link}
	   
	   
	       AND ban=#{mon.limit}
	   
	   LIMIT #{start},#{row}

	   SELECT COUNT(*) FROM t_monster WHERE 1=1
	   
	       AND hash_code IN
           
	            #{id}
           
	   
	   
	       AND c_name LIKE CONCAT('%',#{mon.name},'%')
	   
	   
	       AND lev=#{mon.lev}
	   
	   
	       AND attribute IN
           
	            #{attribute}
           
	   
	   
	       AND race IN
           
	            #{race}
           
	   
	   
	       AND atk=#{mon.atk}
	   
	   
	       AND def=#{mon.def}
	   
	   
	       AND pend=#{mon.pend}
	   
	   
	       AND link=#{mon.link}
	   
	   
	       AND ban=#{mon.limit}
